sys_script_include.script:
var GetEmailAddrress = Class.create();
GetEmailAddrress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getEmail: function()
	{
		var userRecord = new GlideRecord("sys_user");
		userRecord.get(this.getParameter('sysparm_userID'));
		return userRecord.email + '';
	},
    type: 'GetEmailAddrress'
});
